import React, { Component } from 'react';
import axios from 'axios';
import { Table, Popconfirm, message  } from 'antd';
import { EditTwoTone, DeleteOutlined } from '@ant-design/icons'
import I18n from "i18n-js";
  
// const data = [
//     {
//       key: '1',
//       name: 'John Brown',
//       age: 32,
//       email: 'jb@gmail.com',
//       address: 'New York No. 1 Lake Park',
//       order_item: 'T_Shirt',
//     //   tags: ['nice', 'developer'],
//     },
//     {
//       key: '2',
//       name: 'Jim Green',
//       age: 42,
//       email: 'jg@gmail.com',
//       address: 'London No. 1 Lake Park',
//       order_item: 'T_Shirt',
//     //   tags: ['loser'],
//     },
//     {
//       key: '3',
//       name: 'Joe Black',
//       age: 32,
//       email: 'jbl@gmail.com',
//       address: 'Sidney No. 1 Lake Park',
//       order_item: 'T_Shirt',
//     //   tags: ['cool', 'teacher'],
//     },
//   ];

class CustomerTable extends React.Component {

    render() { 
      const columns = [
        {
          title: I18n.t("customer.id"),
          dataIndex: 'id',
          key: 'number',
        },
        {
          title: I18n.t("customer.customerName"),
          dataIndex: 'name',
          key: 'name',
          // render: text => <a>{text}</a>,
        },
        {
          title: I18n.t("customer.age"),
          dataIndex: 'age',
          key: 'age',
        },
        {
          title: I18n.t("customer.email"),
          dataIndex: 'email',
          key: 'email',
        },
        {
          title: I18n.t("customer.address"),
          dataIndex: 'address',
          key: 'address',
        },
        {
          title: I18n.t("customer.orderItem"),
          dataIndex: 'order_item',
          key: 'order_item',
        },
        // {
        //   title: 'Action',
        //   key: 'action',
        //   render: (text, record) => (
        //     <Space size="middle">
        //       {/* <a>Invite {record.name}</a> */}
        //       <a>Edit</a>
        //       <a>Delete</a>
        //     </Space>
        //   ),
        // },
        {
          title: I18n.t("customer.action"),
          key: "action",
          render: (_text, record) => (
            <>
            <EditTwoTone style={{marginLeft: 50}} onClick={() => this.onEditCustomer(record.id)}/>
            <Popconfirm title="Are you sure to delete this customer?" 
            onConfirm={() => this.deleteCustomer(record.id)} 
            okText="Yes"
            okType="danger" 
            cancelText="No"
            >
              {/* <a href="#" type="danger">
                Delete{" "}
              </a> */}
              <DeleteOutlined style={{ color: "red", marginLeft: 12}}/>
            </Popconfirm>
           
            </>
          ),
          
        },
    ];

    ////const data = this.state.customers;
    ////console.log(data);
    //  const datasource = 
    //    { data.map((customer, index) => { 
    //     [
    //       {
    //         key: {customer.id},
    //         number: {customer.item.title} -{" "},
    //         name: 32,
    //         price: 'NGN' {customer.item.price} ,
    //         quantity: {customer.quantity},
    //       },
    //     ];
    //    })
    //   };

   

        return (
        <Table 
          columns={columns} 
          dataSource={this.state.customers} 
          // dataSource={datasource}
          bordered 
          pagination={{ pageSize: 5, showSizeChanger: false }} 
        />
        )
    }
};
 
export default CustomerTable;
