import React, { Component } from "react";
// import { Select, Input } from 'antd';
import { Select, Form, InputNumber, Button, Input } from 'antd';
import I18n from "i18n-js";
import FormItem from "antd/lib/form/FormItem";
const { Option } = Select;
const { TextArea } = Input;
const layout = {
    labelCol: { span: 8 },
    wrapperCol: { span: 16 },
  };
  
  /* eslint-disable no-template-curly-in-string */
  const validateMessages = {
    required: '${label} is required!',
    types: {
      email: '${label} is not a valid email!',
      number: '${label} is not a valid number!',
    },
    number: {
      range: '${label} must be between ${min} and ${max}',
    },
  };
  /* eslint-enable no-template-curly-in-string */
  
    const onFinish = (values) => {
      console.log(values);
    };
class CustomerForm extends Component {
    render() {
        function handleChange(value) {
            console.log(`selected ${value}`);
          }
         return (
        <div style={{
            display: 'block', width: 700, padding: 30
        }}>
                 
            <Form {...layout} name="nest-messages" onFinish={onFinish} validateMessages={validateMessages}>
                <Form.Item name={['user', 'name']} label={I18n.t("customer.customerName")} rules={[{ required: true }]}>
                    <Input />
                </Form.Item>
                <Form.Item name={['user', 'age']} label={I18n.t("customer.age")} rules={[{ type: 'number', min: 0, max: 99 }]}>
                    <InputNumber />
                </Form.Item>
                <Form.Item name={['user', 'email']} label={I18n.t("customer.email")} rules={[{ type: 'email' }]}>
                    <Input />
                </Form.Item>
                <Form.Item name={['user', 'address']} label={I18n.t("customer.address")} rules={[{ type: 'string' }]}>
                    <Input />
                </Form.Item>
                <Form.Item name={['user', 'order_item']} label={I18n.t("customer.orderItem")}>
                    <Select defaultValue="watch" style={{ width: 120 }}>
                        <Option value="phone">Phone</Option>
                        <Option value="watch">Watch</Option>
                        <Option value="computer">Computer</Option>
                    </Select>
                </Form.Item>    
                <Form.Item name={['user', 'remark']} label={I18n.t("customer.remark")}>
                    <Input.TextArea />
                </Form.Item>
                <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
                    <Button type="primary" htmlType="submit">
                    {I18n.t("common.button.save")}
                    </Button>
                </Form.Item>
            </Form>
        </div>
    );
    }

}

export default CustomerForm;
