import React, { Component } from "react";
import { Select, Form, InputNumber, Button, Input, Alert, message } from 'antd';
import I18n from "i18n-js";
const { Option } = Select;
const layout = {
    labelCol: { span: 8 },
    wrapperCol: { span: 16 },
  };
  
  /* eslint-disable no-template-curly-in-string */
  /* eslint-enable no-template-curly-in-string */

  // createPostRequest = (event) => {
  //   console.log('this.state', this.state);
  //   fetch('/api/v1/posts', {
  //     method: 'post',
  //     body: JSON.stringify(this.state),
  //     headers: { 'Content-Type': 'application/json' },
  //   }).then((response) => {
  //     alert('Post created successfully');
  //     location.href = '/';
  //   });
  // }
  
    // const onFinish = (values) => {
    //   console.log('Finish:', values);
    //   fetch('/api/v1/customers', {
    //     method: 'post',
    //     body: JSON.stringify(values),
    //     headers: { 'Content-Type': 'application/json' },
    //   }).then((response) => {
    //     // alert('Post created successfully');
    //     // console.log(response.json());
    //     message.success('Customer created successful!');
    //     //window.location.reload(false);
    //     // location.href = '/customers';
    //     // success();
    //   });
    // };

const CustomerForm = (porps) => {
  const [onFinish] = porps;

  const validateMessages = {
    required: '${label}は必須です!',
    types: {
      email: '${label}は有効なメールではありません!',
      number: '${label}は有効な番号ではありません!',
    },
    number: {
      range: '${label}は${min}以上${max}以下です！',
    },
  };
  
  return ( 
    <div style={{
      display: 'block', width: 700, padding: 30
  }}>
           
      <Form {...layout} name="nest-messages" onFinish={onFinish} validateMessages={validateMessages}>
      {/* <Alert message="Customer created successful!" type="success" /> */}
          <Form.Item name='name' label={I18n.t("customer.customerName")} rules={[{ required: true }]}>
              <Input />
          </Form.Item>
          <Form.Item name='age' label={I18n.t("customer.age")} rules={[{ type: 'number', min: 0, max: 99 }]}>
              <InputNumber />
          </Form.Item>
          <Form.Item name='email' label={I18n.t("customer.email")} rules={[{ required: true, type: 'email' }]}>
              <Input />
          </Form.Item>
          <Form.Item name='address' label={I18n.t("customer.address")} rules={[{ required: true, type: 'string' }]}>
              <Input />
          </Form.Item>
          <Form.Item name='order_item' label={I18n.t("customer.orderItem")}>
              <Select placeholder="アイテムを選択" style={{ width: 150 }}>
                  <Option value="phone">Phone</Option>
                  <Option value="watch">Watch</Option>
                  <Option value="computer">Computer</Option>
              </Select>
          </Form.Item>    
          <Form.Item name='remark' label={I18n.t("customer.remark")}>
              <Input.TextArea />
          </Form.Item>
          <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
              <Button type="primary" htmlType="submit">
              {I18n.t("common.button.save")}
              </Button>
          </Form.Item>
      </Form>
  </div>
   );
}
 
export default CustomerForm;
