import React from "react";
import { Breadcrumb, Layout } from "antd";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import SideBar from "../home/_sideBar";
import CustomerForm from "./_form.jsx";
import CustomerTable from "./_table.jsx";
const { Content } = Layout;


  onFinish = (values) => {
  console.log('Finish:', values);
  fetch('/api/v1/customers', {
    method: 'post',
    body: JSON.stringify(values),
    headers: { 'Content-Type': 'application/json' },
  }).then((response) => {
    const data = {
          key: response.id,
          id: response.id,
          name: response.name,
          age: response.age,
          email: response.email,
          address: response.address,
          order_item: response.order_item,
          remark: response.remark
    };
    this.setState((prevState) => ({
      customers: [...prevState.customers, data],
  }));
    // alert('Post created successfully');
    // console.log(response.json());
    message.success('Customer created successful!');
    //window.location.reload(false);
    // location.href = '/customers';
    // success();
  });
};

class Customer extends React.Component {
  render() {
    return (
      <Layout style={{ minHeight: "100vh" }}>
        <SideBar />
        <Layout className="site-layout">
          <Content style={{ margin: "0 16px" }}>
            <Breadcrumb style={{ margin: "16px 0" }}>
              <Breadcrumb.Item>{I18n.t("home.home")}</Breadcrumb.Item>
              <Breadcrumb.Item>{I18n.t("home.menu1.side1")}</Breadcrumb.Item>
            </Breadcrumb>
            <div
              className="site-layout-background"
              style={{ padding: 24, minHeight: 360 }}
            >
              {/* This is Customer Page */}
              <CustomerForm onFinish={this.onFinish} />
              <CustomerTable />
            </div>
          </Content>
        </Layout>
      </Layout>
    );
  }
}

export default Customer;
