import React, { Component } from "react";
import { Select, Form, InputNumber, Button, Input } from "antd";
import I18n from "i18n-js";

const { Option } = Select;
const layout = {
  labelCol: { span: 8 },
  wrapperCol: { span: 16 },
};

const CustomerForm = (props) => {
  const { onFinish, editData } = props;

  const [form] = Form.useForm();

  const onReset = () => {
    form.resetFields();
  };

  const validateMessages = {
    required: "${label}は必須です!",
    types: {
      email: "${label}は有効なメールではありません!",
      number: "${label}は有効な番号ではありません!",
    },
    number: {
      range: "${label}は${min}以上${max}以下です！",
    },
  };
  return (
    <div
      style={{
        display: "block",
        width: 700,
        padding: 15,
      }}
    >
      <Form
        {...layout}
        form={form}
        name="nest-messages"
        onFinish={onFinish}
        ref={editData}
        validateMessages={validateMessages}
      >
        {/* <Alert message="Customer created successful!" type="success" /> */}
        <Form.Item name="id"></Form.Item>

        <Form.Item
          name="name"
          label={I18n.t("customer.customerName")}
          rules={[{ required: true }]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name="age"
          label={I18n.t("customer.age")}
          rules={[{ type: "number", min: 0, max: 99 }]}
        >
          <InputNumber />
        </Form.Item>
        <Form.Item
          name="email"
          label={I18n.t("customer.email")}
          rules={[{ required: true, type: "email" }]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          name="address"
          label={I18n.t("customer.address")}
          rules={[{ required: true, type: "string" }]}
        >
          <Input />
        </Form.Item>
        <Form.Item name="order_item" label={I18n.t("customer.orderItem")}>
          <Select placeholder="アイテムを選択" style={{ width: 150 }}>
            <Option value="phone">Phone</Option>
            <Option value="watch">Watch</Option>
            <Option value="computer">Computer</Option>
          </Select>
        </Form.Item>
        <Form.Item name="remark" label={I18n.t("customer.remark")}>
          <Input.TextArea />
        </Form.Item>
        <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
          <Button type="primary" htmlType="submit">
            {I18n.t("common.button.save")}
          </Button>

          <Button
            style={{ marginLeft: 30 }}
            type="primary"
            htmlType="button"
            onClick={onReset}
          >
            {I18n.t("common.button.clear")}
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default CustomerForm;
