const CustomerTable = (props) => {
  const { onDeleteCustomer, onEditCustomer, customerData } = props;
  
  const columns = [
    {
      title: I18n.t("customer.id"),
      dataIndex: 'id',
      key: 'number',
    },
    {
      title: I18n.t("customer.customerName"),
      dataIndex: 'name',
      key: 'name',
      // render: text => <a>{text}</a>,
    },
    {
      title: I18n.t("customer.age"),
      dataIndex: 'age',
      key: 'age',
    },
    {
      title: I18n.t("customer.email"),
      dataIndex: 'email',
      key: 'email',
    },
    {
      title: I18n.t("customer.address"),
      dataIndex: 'address',
      key: 'address',
    },
    {
      title: I18n.t("customer.orderItem"),
      dataIndex: 'order_item',
      key: 'order_item',
    },
    {
      title: I18n.t("customer.action"),
      key: "action",
      render: (_text, record) => (
        <>
        <EditTwoTone 
        style={{marginLeft: 50}} 
            onClick={() => { onDeleteCustomer(record.id) }}
        />

        <Popconfirm title="Are you sure to delete this customer?" 
            onConfirm={() => { onEditCustomer(record.id) }}
        okText="Yes"
        okType="danger" 
        cancelText="No"
        >
        <DeleteOutlined style={{ color: "red", marginLeft: 12}}/>
        </Popconfirm>
       
        </>
      ),
      
    },
];

  return ( 
        <Table 
          columns={columns} 
          dataSource={customerData} 
          // dataSource={datasource}
          bordered 
          pagination={{ pageSize: 5, showSizeChanger: false }} 
        />
    );
}
 
export default CustomerTable;
