import React, { Component } from 'react';
import axios from 'axios';
import { Table, Tag, Space } from 'antd';

  
// const data = [
//     {
//       key: '1',
//       name: 'John Brown',
//       age: 32,
//       email: 'jb@gmail.com',
//       address: 'New York No. 1 Lake Park',
//       order_item: 'T_Shirt',
//     //   tags: ['nice', 'developer'],
//     },
//     {
//       key: '2',
//       name: 'Jim Green',
//       age: 42,
//       email: 'jg@gmail.com',
//       address: 'London No. 1 Lake Park',
//       order_item: 'T_Shirt',
//     //   tags: ['loser'],
//     },
//     {
//       key: '3',
//       name: 'Joe Black',
//       age: 32,
//       email: 'jbl@gmail.com',
//       address: 'Sidney No. 1 Lake Park',
//       order_item: 'T_Shirt',
//     //   tags: ['cool', 'teacher'],
//     },
//   ];

class CustomerTable extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      customers: [],
    };
  }

  

  componentDidMount() {
    axios
      .get("api/v1/customers.json")
      .then((response) => {
        // console.log(response.data);
        this.setState({
            customers: response.data,
        });
      })
      .catch((error) => console.log(error));
  }


    render() { 


      const columns = [
        {
          title: 'Number',
          dataIndex: 'id',
          key: 'number',
          render: text => <a>{text}</a>,
        },
        {
          title: 'Name',
          dataIndex: 'name',
          key: 'name',
          // render: text => <a>{text}</a>,
        },
        {
          title: 'Age',
          dataIndex: 'age',
          key: 'age',
        },
        {
          title: 'Email',
          dataIndex: 'email',
          key: 'email',
        },
        {
          title: 'Address',
          dataIndex: 'address',
          key: 'address',
        },
        {
          title: 'Order_item',
          dataIndex: 'order_item',
          key: 'order_item',
        },
        {
          title: 'Action',
          key: 'action',
          render: (text, record) => (
            <Space size="middle">
              {/* <a>Invite {record.name}</a> */}
              <a>Edit</a>
              <a>Delete</a>
            </Space>
          ),
        },
    ];

    // const data = this.state.customers;
    // const datasource = 
    //   { data.customers.map((customer, i) => { 
    //     [
    //       {
    //         key: {customer.id},
    //         number: {customer.item.title} -{" "},
    //         name: 32,
    //         price: 'NGN' {customer.item.price} ,
    //         quantity: {customer.quantity},
    //       },
    //     ];
    //    })
    //   };

        return (
        <Table 
          columns={columns} 
          dataSource={this.state.customers} 
          // dataSource={datasource}
          bordered 
          pagination={{ pageSize: 3, showSizeChanger: true }} 
        />
        )
    }
};
 
export default CustomerTable;
