import React, { Component, useState } from "react";
import { Select, Form, InputNumber, Button, Input, DatePicker, Modal, Radio } from "antd";
import I18n from "i18n-js";
// import React, { useState } from "react";

const { Option } = Select;
const layout = {
  labelCol: { span: 10 },
  wrapperCol: { span: 14 },
};

const CustomerForm = (props) => {
  const { onFinish, editData, itemData, groupData } = props;
  // console.log(itemData);

  const [form] = Form.useForm();

  const [isModalVisible, setIsModalVisible] = useState(false);
  const onChange = (value) => {
  componentEdit.current?.setFieldsValue({
  group_name: value
  });

  setIsModalVisible(false);
  };

  const showModal = () => {
  setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  const onReset = () => {
    form.resetFields();
  };

  const validateMessages = {
    required: "${label}は必須です!",
    types: {
      email: "${label}は有効なメールではありません!",
      number: "${label}は有効な番号ではありません!",
    },
    number: {
      range: "${label}は${min}以上${max}以下です！",
    },
  };
  return (
    <div
      style={{
        display: "block",
        width: 700,
        // padding: 15,
      }}
    >
      <Form
        {...layout}
        form={form}
        name="nest-messages"
        onFinish={onFinish}
        autoComplete="off"
        ref={editData}
        validateMessages={validateMessages}
      >
        {/* <Alert message="Customer created successful!" type="success" /> */}
        <Form.Item name="id"></Form.Item>
        <Form.Item
          name="name"
          label={I18n.t("customer.customerName")}
          rules={[{ required: true }]}
        >
          <Input style={{ width: "60%" }} />
        </Form.Item>
        <Form.Item
          name="age"
          label={I18n.t("customer.age")}
          rules={[{ required: true, type: "number", min: 0, max: 99 }]}
        >
          <InputNumber />
        </Form.Item>
        <Form.Item
          name="email"
          label={I18n.t("customer.email")}
          rules={[{ required: true, type: "email" }]}
        >
          <Input style={{ width: "60%" }} />
        </Form.Item>
        <Form.Item
          name="address"
          label={I18n.t("customer.address")}
          rules={[{ required: true, type: "string" }]}
        >
          <Input style={{ width: "60%" }} />
        </Form.Item>
        <Form.Item
          name="order_id"
          label={I18n.t("customer.orderItem")}
          rules={[{ required: true }]}
        >
          <Select placeholder="アイテムを選択" style={{ width: 150 }}>
            {itemData.map((item) => (
              <Select.Option key={item.id} value={item.id}>
                {item.name}
              </Select.Option>
            ))}
          </Select>

          {/* <Select placeholder="アイテムを選択" style={{ width: 150 }}>
            <Option value="phone">Phone</Option>
            <Option value="watch">Watch</Option>
            <Option value="computer">Computer</Option>
          </Select> */}
        </Form.Item>

        <Form.Item
            label={I18n.t("customer.groupName")}
            name="group_name"
            rules={[
            {
            required: true,
            },
            ]}
            >
            <Select onClick={showModal} defaultValue="一つ選択してください" style={{ width: "60%" }}>
            {/* {company.map((value) => (
            <Select.Option key={value.id} value={value.id}>{value.name}</Select.Option>
            ))} */}
            </Select>

            </Form.Item>

        <Form.Item
          name="order_date"
          label={I18n.t("customer.orderDate")}
          rules={[{ required: true }]}
        >
          <DatePicker />
        </Form.Item>
        <Form.Item
          direction="vertical"
          name="remark"
          label={I18n.t("customer.remark")}
          rules={[
            {
              max: 20,
              message: I18n.t("message.M005"),
            },
          ]}
        >
          <Input.TextArea style={{ width: "60%", resize: "none" }} />
        </Form.Item>
        <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
          <Button
            type="primary"
            htmlType="submit"
            // onMouseUp={onReset}
            style={{ marginLeft: "15%" }}
          >
            {I18n.t("common.button.save")}
          </Button>
          <Button
            style={{ marginLeft: 30 }}
            type="primary"
            htmlType="button"
            onClick={onReset}
          >
            {I18n.t("common.button.clear")}
          </Button>
        </Form.Item>
      </Form>

      <Modal title="グループ名" visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}>
        <Radio.Group name="radiogroup">
        {groupData.map((value) => (
        <Radio 
          key={value.id} 
          value = { value.id } 
          onChange={() => onChange(value.name)}　
          >{value.name}
          </Radio>
          ))}
        </Radio.Group>
      </Modal>
    </div>
  );
};

export default CustomerForm;
