import React from "react";
import { Breadcrumb, Layout, message, Input, Button, Space } from "antd";
import I18n from "i18n-js";
import "../../bundles/i18n/ja.js";
import axios from "axios";
import SideBar from "../home/_sideBar";
import CustomerForm from "./_form.jsx";
import CustomerTable from "./_table.jsx";
import moment from "moment";
import Highlighter from "react-highlight-words";
import { SearchOutlined } from "@ant-design/icons";
const { Content } = Layout;

class Customer extends React.Component {
  formRef = React.createRef();
  state = {
    customers: [],
    items: [],
    groups: [],
    searchText: "",
    searchedColumn: "",
  };

  // 検索機能
  getColumnSearchProps = (dataIndex) => ({
    filterDropdown: ({
      setSelectedKeys,
      selectedKeys,
      confirm,
      clearFilters,
    }) => (
      <div style={{ padding: 8 }}>
        <Input
          ref={(node) => {
            this.searchInput = node;
          }}
          placeholder={`Search ${dataIndex}`}
          value={selectedKeys[0]}
          onChange={(e) =>
            setSelectedKeys(e.target.value ? [e.target.value] : [])
          }
          onPressEnter={() =>
            this.handleSearch(selectedKeys, confirm, dataIndex)
          }
          style={{ marginBottom: 8, display: "block" }}
        />
        <Space>
          <Button
            type="primary"
            onClick={() => this.handleSearch(selectedKeys, confirm, dataIndex)}
            icon={<SearchOutlined />}
            size="small"
            style={{ width: 90 }}
          >
            Search
          </Button>
          <Button
            onClick={() => this.handleReset(clearFilters)}
            size="small"
            style={{ width: 90 }}
          >
            Reset
          </Button>
          <Button
            type="link"
            size="small"
            onClick={() => {
              confirm({ closeDropdown: false });
              this.setState({
                searchText: selectedKeys[0],
                searchedColumn: dataIndex,
              });
            }}
          >
            Filter
          </Button>
        </Space>
      </div>
    ),
    filterIcon: (filtered) => (
      <SearchOutlined style={{ color: filtered ? "#1890ff" : undefined }} />
    ),
    onFilter: (value, record) =>
      record[dataIndex]
        ? record[dataIndex]
            .toString()
            .toLowerCase()
            .includes(value.toLowerCase())
        : "",
    onFilterDropdownVisibleChange: (visible) => {
      if (visible) {
        setTimeout(() => this.searchInput.select(), 100);
      }
    },
    render: (text) =>
      this.state.searchedColumn === dataIndex ? (
        <Highlighter
          highlightStyle={{ backgroundColor: "#ffc069", padding: 0 }}
          searchWords={[this.state.searchText]}
          autoEscape
          textToHighlight={text ? text.toString() : ""}
        />
      ) : (
        text
      ),
  });

  // テブル検索技能
  handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    this.setState({
      searchText: selectedKeys[0],
      searchedColumn: dataIndex,
    });
  };

  // 検索リセット技能
  handleReset = (clearFilters) => {
    clearFilters();
    this.setState({ searchText: "" });
  };

  // ロード機能

  componentDidMount() {
    this.loadCustomers();
  }

  loadCustomers = () => {
    axios
      .get("api/v1/customers.json")
      .then((response) => {
        const data = response.data.customers;
        const item = response.data.items;
        const group = response.data.groups;
        console.log("Customer", data);
        // console.log(customer, item);
        // const data = response.data;
        data.forEach((element) => {
          const customerData = {
            key: element.customer_id,
            id: element.customer_id,
            name: element.name,
            age: element.age,
            email: element.email,
            address: element.address,
            order_item: element.item_name,
            group_name: element.group_name,
            order_date: element.order_date,
            remark: element.remark,
          };

          this.setState((prevState) => ({
            customers: [...prevState.customers, customerData],
            items: item,
            groups: group,
          }));
        });
      })
      .catch((error) => console.log(error));
  };

  reloadCustomers = () => {
    this.setState({ customers: [] });
    this.loadCustomers();
  };

  // 挿入機能 または 更新機能
  onFinish = (values) => {
    console.log("Finish:", values);
    if (values.id == undefined) {
      fetch("/api/v1/customers", {
        method: "post",
        body: JSON.stringify(values),
        headers: { "Content-Type": "application/json" },
      })
        .then((res) => res.json())
        .then((response) => {
          message.success(I18n.t("message.M002"));
          this.reloadCustomers();
          // location.href = '/customers';
        });
    } else {
      // console.log("Finish:", values);
      fetch(`/api/v1/customers/update/${values.id}`, {
        method: "post",
        body: JSON.stringify(values),
        headers: { "Content-Type": "application/json" },
      })
        .then((res) => res.json())
        .then((response) => {
          const data = {
            key: response.id,
            id: response.id,
            name: response.name,
            age: response.age,
            email: response.email,
            address: response.address,
            order_item: response.order_item,
            order_date: response.order_date,
            remark: response.remark,
          };
          this.setState((prevState) => ({
            customers: [...prevState.customers, data],
          }));
          // alert('Post created successfully');
          message.success(I18n.t("message.M003"));
          this.reloadCustomers();
          // location.href = "/customers";
        });
    }
  };

  // 削除機能
  handleDeleteCustomer = (id) => {
    const url = `api/v1/customers/${id}`;
    fetch(url, {
      method: "delete",
    })
      .then((response) => {
        this.setState({
          customers: this.state.customers.filter(
            (customer) => customer.key !== id
          ),
        });
        message.success(I18n.t("message.M004"));
      })
      .catch((err) => message.error("Error: " + err));
  };

  // ハンドル編集機能
  handleEdit = (record) => {
    this.formRef.current?.setFieldsValue({
      id: record.id,
      name: record.name,
      age: record.age,
      email: record.email,
      address: record.address,
      order_id: record.order_id,
      order_date: moment(record.order_date, "YYYY MM DD"),
      remark: record.remark,
    });
    console.log("Edit Data", this.formRef);
  };

  render() {
    return (
      <Layout style={{ minHeight: "100vh" }}>
        <SideBar />
        <Layout className="site-layout">
          <Content style={{ margin: "0 16px" }}>
            <Breadcrumb style={{ margin: "16px 0" }}>
              <Breadcrumb.Item>{I18n.t("home.home")}</Breadcrumb.Item>
              <Breadcrumb.Item>{I18n.t("home.menu1.side2")}</Breadcrumb.Item>
            </Breadcrumb>
            <div
              className="site-layout-background"
              style={{ padding: 24, minHeight: 360 }}
            >
              {/* This is Customer Page */}
              <CustomerForm
                onFinish={this.onFinish}
                itemData={this.state.items}
                groupData={this.state.groups}
                editData={this.formRef}
              />
              <CustomerTable
                colmnSearch={this.getColumnSearchProps}
                customerData={this.state.customers}
                itemData={this.state.items}
                groupData={this.state.groups}
                onEdit={this.handleEdit}
                onDeleteCustomer={this.handleDeleteCustomer}
              />
            </div>
          </Content>
        </Layout>
      </Layout>
    );
  }
}

export default Customer;
