import React, { Component, useState } from "react";
import {
  Select,
  Form,
  InputNumber,
  Button,
  Input,
  DatePicker,
  Modal,
  Radio,
} from "antd";
import I18n from "i18n-js";
import moment from "moment";

const { Option } = Select;
const layout = {
  labelCol: { span: 10 },
  wrapperCol: { span: 14 },
};
const { Search } = Input;

const CustomerForm = (props) => {
  const { onFinish, editData, itemData, groupData, excelExport } = props;
  const [form] = Form.useForm();
  var value1 = React.useState(editData.group_id.value.data);

  const [isModalVisible, setIsModalVisible] = useState(false);

  // ラジオボタンのデータ変化技能
  const onChange = (id, value) => {
    editData.current?.setFieldsValue({
      group_id: id,
      group_name: value,
    });
    setIsModalVisible(true);
  };

  // モーダルを見えるようにする
  const showModal = () => {
    setIsModalVisible(true);
  };

  // モーダルオッケー技能
  const handleOk = () => {
    setIsModalVisible(false);
  };

  // モーダルキャンセル技能
  const handleCancel = () => {
    setIsModalVisible(false);
  };

  // フォームフィールドデータをリセット
  const onReset = () => {
    form.resetFields();
  };

  // 過去の日付を選択できないようにする
  const disabledDate = (current) => {
    return current < moment().endOf("day");
  };

  // フォームのValidationチャック
  const validateMessages = {
    required: "${label}は必須です!",
    types: {
      email: "${label}は有効なメールではありません!",
      number: "${label}は有効な番号ではありません!",
    },
    number: {
      range: "${label}は${min}以上${max}以下です!",
    },
  };

  return (
    <div
      style={{
        display: "block",
        width: 700,
        // padding: 15,
      }}
    >
      <Form
        {...layout}
        form={form}
        name="nest-messages"
        onFinish={onFinish}
        autoComplete="off"
        ref={editData}
        validateMessages={validateMessages}
      >
        <Form.Item name="id" style={{ display: "none" }}>
          <Input />
        </Form.Item>
        <Form.Item name="group_id" style={{ display: "none" }}>
          <Input />
        </Form.Item>
        <Form.Item
          name="name"
          label={I18n.t("customer.customerName")}
          rules={[{ required: true }]}
        >
          <Input style={{ width: "60%" }} />
        </Form.Item>
        <Form.Item
          name="age"
          label={I18n.t("customer.age")}
          rules={[{ required: true, type: "number", min: 0, max: 99 }]}
        >
          <InputNumber style={{ width: "60%" }} />
        </Form.Item>
        <Form.Item
          name="email"
          label={I18n.t("customer.email")}
          rules={[{ required: true, type: "email" }]}
        >
          <Input style={{ width: "60%" }} />
        </Form.Item>
        <Form.Item
          name="address"
          label={I18n.t("customer.address")}
          rules={[{ required: true, type: "string" }]}
        >
          <Input style={{ width: "60%" }} />
        </Form.Item>
        <Form.Item
          name="order_id"
          label={I18n.t("customer.orderItem")}
          rules={[{ required: true }]}
        >
          <Select placeholder="アイテムを選択" style={{ width: "60%" }}>
            {itemData.map((item) => (
              <Select.Option key={item.id} value={item.id}>
                {item.name}
              </Select.Option>
            ))}
          </Select>
        </Form.Item>

        <Form.Item
          label={I18n.t("customer.groupName")}
          name="group_name"
          rules={[
            {
              required: true,
            },
          ]}
        >
          <Search
            placeholder="グループ名を選択"
            allowClear
            onSearch={showModal}
            style={{ width: "60%" }}
          />
        </Form.Item>

        <Form.Item
          name="order_date"
          label={I18n.t("customer.orderDate")}
          rules={[{ required: true }]}
        >
          <DatePicker style={{ width: "60%" }} disabledDate={disabledDate} />
        </Form.Item>
        <Form.Item
          name="remark"
          label={I18n.t("customer.remark")}
          rules={[
            {
              max: 20,
              message: I18n.t("message.M005"),
            },
          ]}
        >
          <Input.TextArea style={{ width: "60%", resize: "none" }} />
        </Form.Item>
        <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
          <Button
            type="primary"
            htmlType="submit"
            style={{ marginLeft: "15%" }}
          >
            {I18n.t("common.button.save")}
          </Button>
          <Button
            style={{ marginLeft: 30 }}
            type="primary"
            htmlType="button"
            onClick={onReset}
          >
            {I18n.t("common.button.clear")}
          </Button>

          <Button
            style={{ marginLeft: 30 }}
            type="primary"
            htmlType="button"
            onClick={excelExport}
          >
            {I18n.t("common.button.export")}
          </Button>
        </Form.Item>
      </Form>

      {/* グループ選択のモーダル */}
      <Modal
        title="グループ名"
        visible={isModalVisible}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Radio.Group name="radiogroup" value={value1}>
          {groupData.map((value) => (
            <Radio
              key={value.id}
              value={value.id}
              onChange={() => onChange(value.id, value.name)}
            >
              {value.name}
            </Radio>
          ))}
        </Radio.Group>
      </Modal>
    </div>
  );
};

export default CustomerForm;
