import React, { Component } from 'react';
import axios from 'axios';
import { Table, Popconfirm  } from 'antd';

  
// const data = [
//     {
//       key: '1',
//       name: 'John Brown',
//       age: 32,
//       email: 'jb@gmail.com',
//       address: 'New York No. 1 Lake Park',
//       order_item: 'T_Shirt',
//     //   tags: ['nice', 'developer'],
//     },
//     {
//       key: '2',
//       name: 'Jim Green',
//       age: 42,
//       email: 'jg@gmail.com',
//       address: 'London No. 1 Lake Park',
//       order_item: 'T_Shirt',
//     //   tags: ['loser'],
//     },
//     {
//       key: '3',
//       name: 'Joe Black',
//       age: 32,
//       email: 'jbl@gmail.com',
//       address: 'Sidney No. 1 Lake Park',
//       order_item: 'T_Shirt',
//     //   tags: ['cool', 'teacher'],
//     },
//   ];

class CustomerTable extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      customers: []
    };
  }

  

  componentDidMount() {
    axios
      .get("api/v1/customers.json")
      .then((response) => {
        // console.log(response.data);
        const data = response.data;
        data.forEach((element) => {
          const customerData = {
            key: element.id,
            id: element.id,
            name: element.name,
            age: element.age,
            email: element.email,
            address: element.address,
            order_item: element.order_item,
            remark: element.remark
          };

          this.setState((newState) => ({
            customers: [...newState.customers, customerData],
        }));
        });
      })
      .catch((error) => console.log(error));
  }

  deleteCustomer = (id) => {
    const url = `api/v1/customers/${id}`;
  
    fetch(url, {
      method: "delete",
    })
      .then((response) => {
        // if (data.ok) {
        //   this.reloadBeers();
        //   return data.json();
        // }
        // throw new Error("Network error.");
        location.href = '/customers';
        console.log('Deleted Ok');
      })
      .catch((err) => message.error("Error: " + err));
  };


    render() { 


      const columns = [
        {
          title: 'Number',
          dataIndex: 'id',
          key: 'number',
          // render: text => <a>{text}</a>,
        },
        {
          title: 'Name',
          dataIndex: 'name',
          key: 'name',
          // render: text => <a>{text}</a>,
        },
        {
          title: 'Age',
          dataIndex: 'age',
          key: 'age',
        },
        {
          title: 'Email',
          dataIndex: 'email',
          key: 'email',
        },
        {
          title: 'Address',
          dataIndex: 'address',
          key: 'address',
        },
        {
          title: 'Order_item',
          dataIndex: 'order_item',
          key: 'order_item',
        },
        // {
        //   title: 'Action',
        //   key: 'action',
        //   render: (text, record) => (
        //     <Space size="middle">
        //       {/* <a>Invite {record.name}</a> */}
        //       <a>Edit</a>
        //       <a>Delete</a>
        //     </Space>
        //   ),
        // },
        {
          title: "Action",
          key: "action",
          render: (_text, record) => (
            <>
             <a href="#" >Edit</a>
            <Popconfirm title="Are you sure to delete this customer?" 
            onConfirm={() => this.deleteCustomer(record.id)} 
            okText="Yes" 
            cancelText="No"
            >
              <a href="#" type="danger">
                Delete{" "}
              </a>
            </Popconfirm>
           
            </>
          ),
          
        },
    ];

    ////const data = this.state.customers;
    ////console.log(data);
    //  const datasource = 
    //    { data.map((customer, index) => { 
    //     [
    //       {
    //         key: {customer.id},
    //         number: {customer.item.title} -{" "},
    //         name: 32,
    //         price: 'NGN' {customer.item.price} ,
    //         quantity: {customer.quantity},
    //       },
    //     ];
    //    })
    //   };

   

        return (
        <Table 
          columns={columns} 
          dataSource={this.state.customers} 
          // dataSource={datasource}
          bordered 
          pagination={{ pageSize: 5, showSizeChanger: false }} 
        />
        )
    }
};
 
export default CustomerTable;
