import React, { Component } from "react";
import { Table, Popconfirm, Input, Button, Space } from "antd";
import { EditTwoTone, DeleteOutlined, SearchOutlined } from "@ant-design/icons";
// import { SearchOutlined } from '@ant-design/icons';
import I18n from "i18n-js";

const CustomerTable = (props) => {
  const {
    onDeleteCustomer,
    onEdit,
    customerData,
    colmnSearch,
    handleSearch,
    handleReset,
  } = props;

  const columns = [
    {
      title: I18n.t("customer.id"),
      dataIndex: "id",
      key: "number",
      ...getColumnSearchProps(I18n.t("customer.id")),
    },
    {
      title: I18n.t("customer.customerName"),
      dataIndex: "name",
      key: "name",
      ...getColumnSearchProps("name"),
      // render: text => <a>{text}</a>,
    },
    {
      title: I18n.t("customer.age"),
      dataIndex: "age",
      key: "age",
      ...getColumnSearchProps("age"),
    },
    {
      title: I18n.t("customer.email"),
      dataIndex: "email",
      key: "email",
      ...getColumnSearchProps("email"),
    },
    {
      title: I18n.t("customer.address"),
      dataIndex: "address",
      key: "address",
      ...getColumnSearchProps("address"),
    },
    {
      title: I18n.t("customer.orderItem"),
      dataIndex: "order_item",
      key: "order_item",
    },
    {
      title: I18n.t("customer.orderDate"),
      dataIndex: "order_date",
      key: "order_date",
    },
    {
      title: I18n.t("customer.action"),
      key: "action",
      render: (_text, record) => (
        <>
          <EditTwoTone
            style={{ marginLeft: 35 }}
            onClick={() => {
              onEdit(record);
            }}
          />

          <Popconfirm
            title="削除しますか?"
            onConfirm={() => {
              onDeleteCustomer(record.id);
            }}
            okText="はい"
            okType="danger"
            cancelText="いいえ"
          >
            <DeleteOutlined style={{ color: "red", marginLeft: 12 }} />
          </Popconfirm>
        </>
      ),
    },
  ];

  return (
    <Table
      columns={columns}
      dataSource={customerData}
      // dataSource={datasource}
      bordered
      pagination={{ pageSize: 5, showSizeChanger: false }}
    />
  );
};

export default CustomerTable;
